package com.example.silkroad_iot.ui.client;

import android.content.Intent;
import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.util.Log;
import android.widget.Button;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.silkroad_iot.R;
import com.example.silkroad_iot.data.Department;
import com.example.silkroad_iot.data.EmpresaFb;
import com.example.silkroad_iot.data.User;
import com.example.silkroad_iot.data.UserStore;
import com.example.silkroad_iot.databinding.ActivityClientHomeBinding;
import com.google.firebase.firestore.DocumentSnapshot;
import com.google.firebase.firestore.FirebaseFirestore;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class ClientHomeActivity extends AppCompatActivity {
    private ActivityClientHomeBinding b;
    private final UserStore store = UserStore.get();

    private FirebaseFirestore db;
    private List<EmpresaFb> empresasFB = new ArrayList<>();
    private CompanyAdapter companyAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        b = ActivityClientHomeBinding.inflate(getLayoutInflater());
        setContentView(b.getRoot());
        setSupportActionBar(b.toolbar);

        // Toolbar con el nombre de usuario
        User u = store.getLogged();
        String name = (u != null ? u.getName() : "Usuario");
        b.toolbar.setTitle("Hola " + name);

        // Departamentos
        List<Department> deps = Arrays.asList(
                new Department("Lima"), new Department("Cusco"),
                new Department("Arequipa"), new Department("Piura"),
                new Department("La Libertad")
        );
        b.rvDepartments.setLayoutManager(new LinearLayoutManager(this, RecyclerView.HORIZONTAL, false));
        b.rvDepartments.setAdapter(new DepartmentAdapter(deps, d -> {
            // TODO: Filtrar tours por departamento
        }));

        //  Configuración Firebase
        db = FirebaseFirestore.getInstance();
        companyAdapter = new CompanyAdapter(empresasFB);

        b.rvCompanies.setLayoutManager(new LinearLayoutManager(this));
        b.rvCompanies.setAdapter(companyAdapter);

        // Cargar empresas desde Firestore
        cargarEmpresasDesdeFirebase();

        // Filtro
        b.inputSearch.addTextChangedListener(new TextWatcher() {
            @Override public void beforeTextChanged(CharSequence s, int start, int count, int after) {}
            @Override public void onTextChanged(CharSequence s, int start, int before, int count) {
                companyAdapter.filterList(s.toString());
            }
            @Override public void afterTextChanged(Editable s) {}
        });

        // Botón historial
        Button btnHistory = findViewById(R.id.btnHistory);
        btnHistory.setOnClickListener(v -> {
            Intent i = new Intent(this, TourHistoryActivity.class);
            startActivity(i);
        });
    }

    private void cargarEmpresasDesdeFirebase() {
        Log.d("EMPRESAS_FIREBASE", "Iniciando carga desde Firestore...");

        db.collection("empresas")
                .get()
                .addOnSuccessListener(querySnapshot -> {
                    List<EmpresaFb> nuevasEmpresas = new ArrayList<>();

                    for (DocumentSnapshot doc : querySnapshot) {
                        EmpresaFb emp = doc.toObject(EmpresaFb.class);
                        emp.setId(doc.getId());  // ✅ guardar el ID del documento
                        if (emp != null) {
                            nuevasEmpresas.add(emp);
                            Log.d("EMPRESAS_FIREBASE", "Empresa cargada: " + emp.getNombre());
                        }
                    }

                    Log.d("EMPRESAS_FIREBASE", "Total empresas cargadas: " + nuevasEmpresas.size());

                    // ✅ Actualizar adapter con los nuevos datos
                    runOnUiThread(() -> {
                        companyAdapter.updateData(nuevasEmpresas);
                        Log.d("EMPRESAS_FIREBASE", "Adapter items después: " + companyAdapter.getItemCount());
                    });
                })
                .addOnFailureListener(e -> Log.e("EMPRESAS_FIREBASE", "Error al cargar empresas", e));
    }
}

package com.example.silkroad_iot.ui.client;

import android.content.Intent;
import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.util.Log;
import android.widget.Button;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.silkroad_iot.R;
import com.example.silkroad_iot.data.Department;
import com.example.silkroad_iot.data.EmpresaFb;
import com.example.silkroad_iot.data.User;
import com.example.silkroad_iot.data.UserStore;
import com.example.silkroad_iot.databinding.ActivityClientHomeBinding;
import com.google.firebase.firestore.DocumentSnapshot;
import com.google.firebase.firestore.FirebaseFirestore;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class ClientHomeActivity extends AppCompatActivity {
    private ActivityClientHomeBinding b;
    private final UserStore store = UserStore.get();

    private FirebaseFirestore db;
    private List<EmpresaFb> empresasFB = new ArrayList<>();
    private CompanyAdapter companyAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        b = ActivityClientHomeBinding.inflate(getLayoutInflater());
        setContentView(b.getRoot());
        setSupportActionBar(b.toolbar);

        // Toolbar con el nombre de usuario
        User u = store.getLogged();
        String name = (u != null ? u.getName() : "Usuario");
        b.toolbar.setTitle("Hola " + name);

        // Departamentos
        List<Department> deps = Arrays.asList(
                new Department("Lima"), new Department("Cusco"),
                new Department("Arequipa"), new Department("Piura"),
                new Department("La Libertad")
        );
        b.rvDepartments.setLayoutManager(new LinearLayoutManager(this, RecyclerView.HORIZONTAL, false));
        b.rvDepartments.setAdapter(new DepartmentAdapter(deps, d -> {
            // TODO: Filtrar tours por departamento
        }));

        //  Configuración Firebase
        db = FirebaseFirestore.getInstance();
        companyAdapter = new CompanyAdapter(empresasFB);

        // Mostrar companies en Staggered Grid (Pinterest style)
        androidx.recyclerview.widget.StaggeredGridLayoutManager sglm = new androidx.recyclerview.widget.StaggeredGridLayoutManager(2, androidx.recyclerview.widget.StaggeredGridLayoutManager.VERTICAL);
        b.rvCompanies.setLayoutManager(sglm);
        b.rvCompanies.setAdapter(companyAdapter);

        // Recommended tours (horizontal carousel)
        List<com.example.silkroad_iot.data.TourFB> recommended = new ArrayList<>();
        com.example.silkroad_iot.ui.client.TourAdapter recommendedAdapter = new com.example.silkroad_iot.ui.client.TourAdapter(recommended);
        b.rvRecommendedTours.setLayoutManager(new LinearLayoutManager(this, RecyclerView.HORIZONTAL, false));
        b.rvRecommendedTours.setAdapter(recommendedAdapter);

        // Cargar recomendaciones simples desde Firestore (colección "tours" con limit 10)
        db.collection("tours").limit(10).get().addOnSuccessListener(qs -> {
            List<com.example.silkroad_iot.data.TourFB> list = new ArrayList<>();
            for (DocumentSnapshot doc : qs) {
                com.example.silkroad_iot.data.TourFB t = doc.toObject(com.example.silkroad_iot.data.TourFB.class);
                if (t != null) { t.setId(doc.getId()); list.add(t); }
            }
            runOnUiThread(() -> {
                recommendedAdapter.updateData(list);
            });
        }).addOnFailureListener(e -> Log.e("RECOMMENDED", "Error loading tours", e));

        // Cargar empresas desde Firestore
        cargarEmpresasDesdeFirebase();

        // Filtro
        b.inputSearch.addTextChangedListener(new TextWatcher() {
            @Override public void beforeTextChanged(CharSequence s, int start, int count, int after) {}
            @Override public void onTextChanged(CharSequence s, int start, int before, int count) {
                companyAdapter.filterList(s.toString());
            }
            @Override public void afterTextChanged(Editable s) {}
        });

        // Botón historial
        Button btnHistory = findViewById(R.id.btnHistory);
        btnHistory.setOnClickListener(v -> {
            Intent i = new Intent(this, TourHistoryActivity.class);
            startActivity(i);
        });
    }

    private void cargarEmpresasDesdeFirebase() {
        Log.d("EMPRESAS_FIREBASE", "Iniciando carga desde Firestore...");

        db.collection("empresas")
                .get()
                .addOnSuccessListener(querySnapshot -> {
                    List<EmpresaFb> nuevasEmpresas = new ArrayList<>();

                    for (DocumentSnapshot doc : querySnapshot) {
                        EmpresaFb emp = doc.toObject(EmpresaFb.class);
                        emp.setId(doc.getId());  // ✅ guardar el ID del documento
                        if (emp != null) {
                            nuevasEmpresas.add(emp);
                            Log.d("EMPRESAS_FIREBASE", "Empresa cargada: " + emp.getNombre());
                        }
                    }

                    Log.d("EMPRESAS_FIREBASE", "Total empresas cargadas: " + nuevasEmpresas.size());

                    // ✅ Actualizar adapter con los nuevos datos
                    runOnUiThread(() -> {
                        companyAdapter.updateData(nuevasEmpresas);
                        Log.d("EMPRESAS_FIREBASE", "Adapter items después: " + companyAdapter.getItemCount());
                    });
                })
                .addOnFailureListener(e -> Log.e("EMPRESAS_FIREBASE", "Error al cargar empresas", e));
    }
}

package com.example.silkroad_iot.ui.client;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.silkroad_iot.R;

import java.util.List;

public class GalleryAdapter extends RecyclerView.Adapter<GalleryAdapter.VH> {
    private final List<String> images;

    public GalleryAdapter(List<String> images) { this.images = images; }

    static class VH extends RecyclerView.ViewHolder {
        ImageView img;
        VH(@NonNull View v) { super(v); img = v.findViewById(R.id.imgGallery); }
    }

    @NonNull
    @Override
    public VH onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_gallery, parent, false);
        return new VH(v);
    }

    @Override
    public void onBindViewHolder(@NonNull VH holder, int position) {
        String url = images.get(position);
        Glide.with(holder.img.getContext())
                .load(url)
                .placeholder(R.drawable.logo_silkroad)
                .centerCrop()
                .into(holder.img);
    }

    @Override
    public int getItemCount() { return images.size(); }
}

package com.example.silkroad_iot.ui.client;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import com.bumptech.glide.Glide;
import com.example.silkroad_iot.data.TourFB;
import com.example.silkroad_iot.databinding.ActivityTourDetailBinding;

public class TourDetailActivity extends AppCompatActivity {
    ActivityTourDetailBinding b;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        b = ActivityTourDetailBinding.inflate(getLayoutInflater());
        setContentView(b.getRoot());

        setSupportActionBar(b.toolbar);
        getSupportActionBar().setTitle("Detalles del Tour");

        TourFB tour = (TourFB) getIntent().getSerializableExtra("tour");

        if (tour == null) {
            Toast.makeText(this, "No se recibió el tour", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        Log.d("TOUR_DETAIL", "Mostrando detalles de: " + tour.getNombre());

        // Mostrar datos básicos
        b.tTourName.setText(tour.getNombre());
        b.btnAdd.setText("Agregar S/. " + tour.getPrecio());

        Glide.with(this)
                .load(tour.getImagen())
                .into(b.imgTour);

        // Mostrar descripción
        b.tTourDescription.setText(tour.getDescription() != null ? tour.getDescription() : "Bug?");

        // Mostrar campos adicionales
       // b.tTourLangs.setText("Idiomas: " + (tour.getLangs() != null ? tour.getLangs() : "No especificado"));
        b.tTourPeople.setText("Personas: " + tour.getCantidad_personas() + " personas");

        if (tour.getDateFrom() != null)
            b.tTourDateFrom.setText("Inicio: " + new java.text.SimpleDateFormat("dd/MM/yyyy hh:mm a").format(tour.getDateFrom()));
        else
            b.tTourDateFrom.setText("Inicio: -");

        if (tour.getDateTo() != null)
            b.tTourDateTo.setText("Fin: " + new java.text.SimpleDateFormat("dd/MM/yyyy hh:mm a").format(tour.getDateTo()));
        else
            b.tTourDateTo.setText("Fin: -");

        //b.tTourDuration.setText("Duración: " + (tour.getDuration() != null ? tour.getDuration() : "-"));
        b.tTourDuration.setText("Paradas: " + (tour.getId_paradas() != null ? tour.getId_paradas() : "-"));
        // Acción del botón
        b.btnAdd.setOnClickListener(v -> {
            Intent i = new Intent(this, ConfirmTourActivity.class);
            i.putExtra("tour", tour);
            startActivity(i);
        });
    }

}

package com.example.silkroad_iot.ui.client;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import com.bumptech.glide.Glide;
import com.example.silkroad_iot.data.TourFB;
import com.example.silkroad_iot.databinding.ActivityTourDetailBinding;

public class TourDetailActivity extends AppCompatActivity {
    ActivityTourDetailBinding b;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        b = ActivityTourDetailBinding.inflate(getLayoutInflater());
        setContentView(b.getRoot());

        setSupportActionBar(b.toolbar);
        getSupportActionBar().setTitle("Detalles del Tour");

        TourFB tour = (TourFB) getIntent().getSerializableExtra("tour");

        if (tour == null) {
            Toast.makeText(this, "No se recibió el tour", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        Log.d("TOUR_DETAIL", "Mostrando detalles de: " + tour.getNombre());

        // Mostrar datos básicos
        b.tTourName.setText(tour.getNombre());
        b.btnAdd.setText("Agregar S/. " + tour.getPrecio());

        // Gallery: construir lista de imágenes usando imagen principal y posibles imageUrl
        java.util.List<String> images = new java.util.ArrayList<>();
        if (tour.getImagen() != null && !tour.getImagen().isEmpty()) images.add(tour.getImagen());
        if (tour.getImageUrl() != null && !tour.getImageUrl().isEmpty() && !images.contains(tour.getImageUrl())) images.add(tour.getImageUrl());
        // si hubiera paradas con imágenes, agregarlas (paradas opcionales)
        if (tour.getParadas() != null) {
            for (com.example.silkroad_iot.data.ParadaFB p : tour.getParadas()) {
                if (p.getImagen() != null && !p.getImagen().isEmpty()) images.add(p.getImagen());
            }
        }

        if (!images.isEmpty()) {
            GalleryAdapter ga = new GalleryAdapter(images);
            b.vpGallery.setAdapter(ga);
        } else {
            // fallback: mostrar imagen principal en imgTour como antes
            Glide.with(this)
                    .load(tour.getImagen())
                    .into(b.imgTour);
        }

        // Mostrar descripción
        b.tTourDescription.setText(tour.getDescription() != null ? tour.getDescription() : "Bug?");

        // Mostrar campos adicionales
       // b.tTourLangs.setText("Idiomas: " + (tour.getLangs() != null ? tour.getLangs() : "No especificado"));
        b.tTourPeople.setText("Personas: " + tour.getCantidad_personas() + " personas");

        if (tour.getDateFrom() != null)
            b.tTourDateFrom.setText("Inicio: " + new java.text.SimpleDateFormat("dd/MM/yyyy hh:mm a").format(tour.getDateFrom()));
        else
            b.tTourDateFrom.setText("Inicio: -");

        if (tour.getDateTo() != null)
            b.tTourDateTo.setText("Fin: " + new java.text.SimpleDateFormat("dd/MM/yyyy hh:mm a").format(tour.getDateTo()));
        else
            b.tTourDateTo.setText("Fin: -");

        //b.tTourDuration.setText("Duración: " + (tour.getDuration() != null ? tour.getDuration() : "-"));
        b.tTourDuration.setText("Paradas: " + (tour.getId_paradas() != null ? tour.getId_paradas() : "-"));
        // Acción del botón
        b.btnAdd.setOnClickListener(v -> {
            Intent i = new Intent(this, ConfirmTourActivity.class);
            i.putExtra("tour", tour);
            startActivity(i);
        });
    }

}
